// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import MovieList from './components/MovieList';
import Filter from './components/Filter';
import MovieDescriptionPage from './components/MovieDescriptionPage'; // Nouveau composant pour la description
import './styles.css';

function App() {
  const [movies, setMovies] = useState([
    {
      id: 1,
      title: 'Inception',
      description: 'A thief who steals corporate secrets through the use of dream-sharing technology.',
      posterURL: 'https://via.placeholder.com/150',
      rating: 8.8,
      trailerLink: 'https://www.youtube.com/embed/YoHD9XEInc0'
    },
    {
      id: 2,
      title: 'The Dark Knight',
      description: 'Batman faces the Joker, a criminal mastermind who wants to create chaos.',
      posterURL: 'https://via.placeholder.com/150',
      rating: 9.0,
      trailerLink: 'https://www.youtube.com/embed/EXeTwQWrcwY'
    }
  ]);

  const [filterTitle, setFilterTitle] = useState('');
  const [filterRating, setFilterRating] = useState('');

  const filteredMovies = movies.filter((movie) => {
    return (
      movie.title.toLowerCase().includes(filterTitle.toLowerCase()) &&
      (filterRating ? movie.rating >= filterRating : true)
    );
  });

  const addMovie = (newMovie) => {
    setMovies([...movies, newMovie]);
  };

  return (
    <Router>
      <div className="App">
        <h1>Application de Cin√©ma</h1>
        <Filter
          filterTitle={filterTitle}
          filterRating={filterRating}
          setFilterTitle={setFilterTitle}
          setFilterRating={setFilterRating}
        />
        <MovieList movies={filteredMovies} />
        <button onClick={() => addMovie({
          id: 3,
          title: 'Avengers: Endgame',
          description: 'The Avengers assemble to reverse the damage done by Thanos.',
          posterURL: 'https://via.placeholder.com/150',
          rating: 8.4,
          trailerLink: 'https://www.youtube.com/embed/TcMBFSGVi1c'
        })}>
          Ajouter un film
        </button>

        {/* Routes */}
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/movie/:movieId" element={<MovieDescriptionPage movies={movies} />} />
        </Routes>
      </div>
    </Router>
  );
}

// Nouveau composant Home pour afficher la liste des films
const Home = () => (
  <div>
    <h2>Liste des Films</h2>
    <MovieList />
  </div>
);

export default App;
