// src/components/Task.js
import React from 'react';
import { useDispatch } from 'react-redux';
import { toggleTask, removeTask, editTask } from '../actions/taskActions';

const Task = ({ task }) => {
  const dispatch = useDispatch();

  const handleToggle = () => dispatch(toggleTask(task.id));
  const handleRemove = () => dispatch(removeTask(task.id));
  const handleEdit = () => {
    const newDescription = prompt('Edit task:', task.description);
    if (newDescription) {
      dispatch(editTask(task.id, newDescription));
    }
  };

  return (
    <div>
      <p style={{ textDecoration: task.isDone ? 'line-through' : 'none' }}>
        {task.description}
      </p>
      <button onClick={handleToggle}>{task.isDone ? 'Undo' : 'Done'}</button>
      <button onClick={handleEdit}>Edit</button>
      <button onClick={handleRemove}>Delete</button>
    </div>
  );
};

export default Task;
