// src/actions/taskActions.js

export const ADD_TASK = 'ADD_TASK';
export const TOGGLE_TASK = 'TOGGLE_TASK';
export const EDIT_TASK = 'EDIT_TASK';
export const REMOVE_TASK = 'REMOVE_TASK';
export const FILTER_TASKS = 'FILTER_TASKS';

// Action pour ajouter une tâche
export const addTask = (description) => ({
  type: ADD_TASK,
  payload: { description, isDone: false, id: Date.now() },
});

// Action pour marquer une tâche comme terminée
export const toggleTask = (id) => ({
  type: TOGGLE_TASK,
  payload: id,
});

// Action pour éditer une tâche
export const editTask = (id, newDescription) => ({
  type: EDIT_TASK,
  payload: { id, newDescription },
});

// Action pour supprimer une tâche
export const removeTask = (id) => ({
  type: REMOVE_TASK,
  payload: id,
});

// Action pour filtrer les tâches
export const filterTasks = (filter) => ({
  type: FILTER_TASKS,
  payload: filter,
});
