// src/components/ListTask.js
import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { filterTasks } from '../actions/taskActions';
import Task from './Task';

const ListTask = () => {
  const tasks = useSelector((state) => state.taskState.tasks);
  const filter = useSelector((state) => state.taskState.filter);
  const dispatch = useDispatch();

  const filteredTasks =
    filter === 'all'
      ? tasks
      : tasks.filter((task) => (filter === 'done' ? task.isDone : !task.isDone));

  return (
    <div>
      <button onClick={() => dispatch(filterTasks('all'))}>All</button>
      <button onClick={() => dispatch(filterTasks('done'))}>Done</button>
      <button onClick={() => dispatch(filterTasks('notDone'))}>Not Done</button>
      {filteredTasks.map((task) => (
        <Task key={task.id} task={task} />
      ))}
    </div>
  );
};

export default ListTask;
