import React, { useState } from 'react';

const TaskItem = ({ task, onUpdateTask, onDeleteTask }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [taskName, setTaskName] = useState(task.name);
  const [taskDescription, setTaskDescription] = useState(task.description);

  const handleSave = () => {
    onUpdateTask({ ...task, name: taskName, description: taskDescription });
    setIsEditing(false);
  };

  return (
    <div className={`task-item ${task.completed ? 'completed' : ''}`}>
      {isEditing ? (
        <>
          <input
            type="text"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
          <input
            type="text"
            value={taskDescription}
            onChange={(e) => setTaskDescription(e.target.value)}
          />
          <button onClick={handleSave}>Enregistrer</button>
        </>
      ) : (
        <>
          <h3>{task.name}</h3>
          <p>{task.description}</p>
          <button onClick={() => onUpdateTask({ ...task, completed: !task.completed })}>
            {task.completed ? 'Réactiver' : 'Terminer'}
          </button>
          <button onClick={() => setIsEditing(true)}>Modifier</button>
          <button onClick={() => onDeleteTask(task.id)}>Supprimer</button>
        </>
      )}
    </div>
  );
};

export default TaskItem;
